class AutoLoopBannerActivity : BaseActivity() {

    var viewBinding: ActivityLoopBannerBinding? = null
    private var mIndicatorCircleLine: SmoothTransIndicator? = null

    private var maxVppage = 0
    private val mPicUrlList = mutableListOf<String>()
    private var mVpAdapter: VpCarouselAdapter? = null
    private var handler: Handler? = MsgHandler(this)
    private var mCurrentViewPagerPos = -1
    private val VIEWPAGERCHANGE = 0

    override fun onViewCreated() {


        mPicUrlList.add("111data:image/jpeg;base64,")
        mPicUrlList.add("222data:image/jpeg;base64,")
       // mPicUrlList.add("333https://ss0.bdstatic.com/70cFuHSh_Q1YnxGkpoWK1HF6hhy/it/u=1535066694,3814009616&fm=26&gp=0.jpg")

        viewBinding?.vpCarousel?.addOnPageChangeListener(object:OnPageChangeListener{
            override fun onPageScrolled(
                position: Int,
                positionOffset: Float,
                positionOffsetPixels: Int
            ) {

            }

            override fun onPageSelected(position: Int) {
                change(position)
                mCurrentViewPagerPos = position
            }

            override fun onPageScrollStateChanged(state: Int) {
                if (state == ViewPager.SCROLL_STATE_IDLE) {
                    handler?.sendEmptyMessageDelayed(VIEWPAGERCHANGE, 4000)
                } else {
                    handler?.removeMessages(VIEWPAGERCHANGE)
                }
            }

        })




        maxVppage = mPicUrlList.size * 1000 * 100

        val currentPosition: Int = maxVppage / 2

        mVpAdapter = VpCarouselAdapter(this, maxVppage, mPicUrlList)
        viewBinding?.vpCarousel?.adapter = mVpAdapter
        viewBinding?.indicatorCircleLine?.setViewPager(viewBinding?.vpCarousel)
        viewBinding?.indicatorCircleLine?.setNum(mPicUrlList.size)
        viewBinding?.indicatorCircleLine?.setCircleNum(mPicUrlList.size)
        if (mCurrentViewPagerPos != -1 && mCurrentViewPagerPos <= maxVppage) {
            viewBinding?.vpCarousel?.currentItem = mCurrentViewPagerPos
        } else {
            viewBinding?.vpCarousel?.currentItem = currentPosition
        }
    }


    private fun change(position: Int) {
        var tempPosition = position
        if (mPicUrlList.size == 0) {
            return
        }
        tempPosition %= mPicUrlList.size
        LogUtils.tag(Constants.LOG_TAG).d("tempPosition======${tempPosition}")
        //TODO 设置底部的指示器位于哪个位置了
    }

    protected fun swicthPage() {
        viewBinding?.vpCarousel?.let {
            var currentItem: Int = it.currentItem //获取当前显示的界面的索引
            currentItem++
            if (currentItem < maxVppage) {
                it.setCurrentItem(currentItem)
                handler?.sendEmptyMessageDelayed(VIEWPAGERCHANGE, 4000)
            } else {
                handler?.removeMessages(VIEWPAGERCHANGE) //停止给handler发送消息
            }
        }
    }

    class MsgHandler(view: AutoLoopBannerActivity) : Handler() {
        private val mView: WeakReference<AutoLoopBannerActivity> = WeakReference(view)

        override fun handleMessage(msg: Message) {
            mView.get()?.swicthPage()
        }
    }

    override fun onStart() {
        super.onStart()
        handler?.sendEmptyMessageDelayed(VIEWPAGERCHANGE, 4000) //隔多长时间,给handler发送一个消息
    }

    override fun onStop() {
        super.onStop()
        handler?.removeMessages(VIEWPAGERCHANGE) //停止给handler发送消息
    }

    override fun onDestroy() {
        if (handler != null) {
            handler?.removeCallbacksAndMessages(null)
            handler = null
        }
        super.onDestroy()
    }



    override fun getLayoutId(): Int {
        return 0
    }

    override fun getLayoutView(): View? {
        viewBinding = ActivityLoopBannerBinding.inflate(LayoutInflater.from(this))
        viewBinding?.let {
            return it.root
        }
        return null
    }

}